'use strict';

var escapeRegExp = require('../../src/escapeRegExp'),
    expect = require('chai').expect;

describe('escapeRegExp()', function () {
    it('should escape all special chars', function () {
        expect(escapeRegExp('My [ () ] \\ +*. + ? ^ $ string'))
            .to.equal('My \\[ \\(\\) \\] \\\\ \\+\\*\\. \\+ \\? \\^ \\$ string');
    });
});
